"use strict";(self.webpackChunkwebsitev_1=self.webpackChunkwebsitev_1||[]).push([[5356],{65917:a=>{a.exports=JSON.parse('{"label":"IaC","permalink":"/docs/tags/ia-c","allTagsPath":"/docs/tags","count":3,"items":[{"id":"IaC/ansible/2023-04-30-ansible-basic-introduction","title":"Basic introduction of ansible","description":"ansible \u662f\u7d44\u614b\u7ba1\u7406\u5de5\u5177 Configuration management Tool ,\u542b\u6709\u81ea\u52d5\u5316\u5de5\u5177,\u914d\u7f6e\u7cfb\u7d71\u3001\u90e8\u7f72\u8edf\u4ef6\u548c\u7de8\u6392\u66f4\u9ad8\u7d1a\u7684 IT \u4efb\u52d9\uff0c\u4f8b\u5982\u6301\u7e8c\u90e8\u7f72\u6216\u96f6\u505c\u6a5f\u6efe\u52d5\u66f4\u65b0.\u6b64\u90e8\u5206\u4e3b\u8981\u662f\u7528\u5b98\u7db2\u7684\u4ecb\u7d39\u5167\u5bb9,\u5beb\u4e0b\u500b\u4eba\u4e86\u89e3\u5f8c\u7684\u7b46\u8a18,\u8ddf\u505alab\u7684\u7d00\u9304\uff01","permalink":"/docs/IaC/ansible/2023-04-30-ansible-basic-introduction"},{"id":"IaC/Terraform/2023-05-15-basic-introuduction-of-Terraform","title":"Basic introduction of Terraform","description":"HashiCorp \u516c\u53f8\u6253\u9020\u7684 IaC \u5de5\u5177,\u4f7f\u7528\u8005\u900f\u904e\u53ef\u95b1\u8b80\u7684\u8a2d\u5b9a\u6a94\u4ee5\u9054\u5230\u7248\u63a7,\u91cd\u8907\u4f7f\u7528,\u5171\u4eab\u8a2d\u5b9a,\u7ba1\u7406\u672c\u5730\u7aef(on-premises resources)\u8ddf\u96f2\u7aef(cloud resources)\u7531\u4f4e\u968e\u7d44\u4ef6,\u4f8b\u5982:\u8a08\u7b97\u3001\u5b58\u5132\u3001\u7db2\u8def\u8cc7\u6e90,\u5230\u9ad8\u5c64\u7d1a\u7d44\u4ef6,\u4f8b\u5982 DNS record,SAAS \u529f\u80fd\u7b49\u7684\u7ba1\u7406.","permalink":"/docs/IaC/Terraform/2023-05-15-basic-introuduction-of-Terraform"},{"id":"IaC/2023-04-30-importance-of-IaC","title":"The importance of IaC","description":"\u57df\u540d,\u6191\u8b49,\u7db2\u8def\u53ca\u4f3a\u670d\u5668\u74b0\u5883(\u9632\u706b\u7246\u3001subnet \u5207\u5206\u3001\u8def\u7531\u8868\u3001gateway\u3001CDN\u3001security group \u7b49\u7b49),\u61c9\u7528\u7a0b\u5f0f\u90e8\u7f72,\u76e3\u63a7\u7a0b\u5f0f,\u65e5\u8a8c\u7ba1\u7406\u9019\u4e9b\u5728IaC\u904e\u7a0b\u90fd\u6703\u7528\u5230,IaC \u7cbe\u795e\u5c31\u662f\u628a\u9019\u6574\u500b\u904e\u7a0b\u7684\u5efa\u7f6e,\u7528\u7a0b\u5f0f\u7684\u65b9\u5f0f\u5efa\u7acb,\u4e5f\u5c31\u662f\u767c\u751f\u4e86\u4ec0\u9ebc\u4e8b\u60c5,\u600e\u9ebc\u5efa\u7acb\u7684,\u90fd\u662f\u7528\u7a0b\u5f0f\u53bb\u63a7\u5236,\u5982\u679c\u67d0\u6bb5\u639b\u6389\u6216\u88ab\u770b\u4f60\u4e0d\u9806\u773c\u7684\u5de5\u7a0b\u5e2b\u780d\u6389,\u53ef\u4ee5\u8fc5\u901f\u5730\u7528\u7a0b\u5f0f\u57f7\u884c\u5099\u63f4\u65b9\u6848\u628a\u9019\u6bb5\u639b\u6389\u7684\u88dc\u4e0a. \u4ee5\u4e0b\u7684\u5c0f\u7bc0,\u6703\u53bb\u8a0e\u8ad6\u6bcf\u968e\u6bb5 IaC \u6709\u54ea\u4e9b\u5de5\u5177\u53ef\u4ee5\u4f7f\u7528.","permalink":"/docs/IaC/2023-04-30-importance-of-IaC"}]}')}}]);